            org.junit.Assert.assertEquals("UP", update.getFirst());
            inputToUpdate++;
        } else {
            boolean shouldBeModel = (java.lang.Integer.parseInt(message) % 10) == 0;
            org.junit.Assert.assertEquals(shouldBeModel ? "MODEL" : "UP", key);
            if (shouldBeModel) {
                models++;
            } else {
                receivedUpdates++;
            }
        }
    }
    com.cloudera.oryx.lambda.speed.SpeedLayerIT.log.info("Received {} models, {} inputs converted to updates, and {} other updates", models, inputToUpdate, receivedUpdates);
    org.junit.Assert.assertEquals(1, models);
    org.junit.Assert.assertEquals(9, receivedUpdates);
    org.junit.Assert.assertEquals(1000, inputToUpdate);
}

