    org.junit.Assert.assertNotNull(miningSchema);
    java.util.List<org.dmg.pmml.MiningField> miningFields = miningSchema.getMiningFields();
    java.util.List<java.lang.String> expectedFeatureNames = schema.getFeatureNames();
    org.junit.Assert.assertEquals("Wrong number of features", expectedFeatureNames.size(), miningFields.size());
    for (int i = 0; i < expectedFeatureNames.size(); i++) {
        org.dmg.pmml.MiningField miningField = miningFields.get(i);
        java.lang.String expectedFeature = expectedFeatureNames.get(i);
        java.lang.String featureName = miningField.getName().getValue();
        org.junit.Assert.assertEquals("Wrong feature at position " + i, expectedFeature, featureName);
        if (schema.isNumeric(expectedFeature)) {
            org.junit.Assert.assertEquals("Wrong op type for feature + " + featureName, OpType.CONTINUOUS, miningField.getOpType());
            if (schema.isTarget(expectedFeature)) {
                org.junit.Assert.assertEquals("Wrong usage type for feature + " + featureName, FieldUsageType.PREDICTED, miningField.getUsageType());
            } else {
                org.junit.Assert.assertEquals("Wrong usage type for feature + " + featureName, FieldUsageType.ACTIVE, miningField.getUsageType());
                double importance = miningField.getImportance();
                org.junit.Assert.assertTrue("Bad importance value " + importance, (importance >= 0.0) && (importance <= 1.0));
            }
        } else
            if (schema.isCategorical(expectedFeature)) {
                org.junit.Assert.assertEquals("Wrong op type for feature " + featureName, OpType.CATEGORICAL, miningField.getOpType());
                if (schema.isTarget(expectedFeature)) {
                    org.junit.Assert.assertEquals("Wrong usage type for feature " + featureName, FieldUsageType.PREDICTED, miningField.getUsageType());
                } else {
                    org.junit.Assert.assertEquals("Wrong usage type for feature " + featureName, FieldUsageType.ACTIVE, miningField.getUsageType());
                    double importance = miningField.getImportance();
                    org.junit.Assert.assertTrue("Bad importance value " + importance, (importance >= 0.0) && (importance <= 1.0));
                }
            } else {
                org.junit.Assert.assertEquals("Wrong usage type for feature " + featureName, FieldUsageType.SUPPLEMENTARY, miningField.getUsageType());
            }

    }
}

