    org.dmg.pmml.PMML pmml = com.cloudera.oryx.common.pmml.PMMLUtils.buildSkeletonPMML();
    java.util.List<org.dmg.pmml.DataField> dataFields = new java.util.ArrayList<>();
    dataFields.add(new org.dmg.pmml.DataField(org.dmg.pmml.FieldName.create("x"), org.dmg.pmml.OpType.CONTINUOUS, org.dmg.pmml.DataType.DOUBLE));
    dataFields.add(new org.dmg.pmml.DataField(org.dmg.pmml.FieldName.create("y"), org.dmg.pmml.OpType.CONTINUOUS, org.dmg.pmml.DataType.DOUBLE));
    org.dmg.pmml.DataDictionary dataDictionary = new org.dmg.pmml.DataDictionary(dataFields);
    dataDictionary.setNumberOfFields(dataFields.size());
    pmml.setDataDictionary(dataDictionary);
    java.util.List<org.dmg.pmml.MiningField> miningFields = new java.util.ArrayList<>();
    org.dmg.pmml.MiningField xMF = new org.dmg.pmml.MiningField(org.dmg.pmml.FieldName.create("x"));
    xMF.setOpType(OpType.CONTINUOUS);
    xMF.setUsageType(FieldUsageType.ACTIVE);
    miningFields.add(xMF);
    org.dmg.pmml.MiningField yMF = new org.dmg.pmml.MiningField(org.dmg.pmml.FieldName.create("y"));
    yMF.setOpType(OpType.CONTINUOUS);
    yMF.setUsageType(FieldUsageType.ACTIVE);
    miningFields.add(yMF);
    org.dmg.pmml.MiningSchema miningSchema = new org.dmg.pmml.MiningSchema(miningFields);
    java.util.List<org.dmg.pmml.ClusteringField> clusteringFields = new java.util.ArrayList<>();
    clusteringFields.add(new org.dmg.pmml.ClusteringField(org.dmg.pmml.FieldName.create("x")).withCenterField(ClusteringField.CenterField.TRUE));
    clusteringFields.add(new org.dmg.pmml.ClusteringField(org.dmg.pmml.FieldName.create("y")).withCenterField(ClusteringField.CenterField.TRUE));
    java.util.List<org.dmg.pmml.Cluster> clusters = new java.util.ArrayList<>();
    clusters.add(new org.dmg.pmml.Cluster().withId("0").withSize(1).withArray(com.cloudera.oryx.app.pmml.AppPMMLUtils.toArray(1.0, 0.0)));
    clusters.add(new org.dmg.pmml.Cluster().withId("1").withSize(2).withArray(com.cloudera.oryx.app.pmml.AppPMMLUtils.toArray(2.0, -1.0)));
    clusters.add(new org.dmg.pmml.Cluster().withId("2").withSize(3).withArray(com.cloudera.oryx.app.pmml.AppPMMLUtils.toArray(-1.0, 0.0)));
    pmml.getModels().add(new org.dmg.pmml.ClusteringModel(org.dmg.pmml.MiningFunctionType.CLUSTERING, ClusteringModel.ModelClass.CENTER_BASED, clusters.size(), miningSchema, new org.dmg.pmml.ComparisonMeasure(ComparisonMeasure.Kind.DISTANCE).withMeasure(new org.dmg.pmml.SquaredEuclidean()), clusteringFields, clusters));
    return pmml;
}

