    com.google.common.base.Preconditions.checkArgument(features > 0);
    com.google.common.base.Preconditions.checkArgument(lambda >= 0.0);
    com.google.common.base.Preconditions.checkArgument(alpha > 0.0);
    org.apache.spark.api.java.JavaRDD<java.lang.String[]> parsedRDD = trainData.map(com.cloudera.oryx.app.common.fn.MLFunctions.PARSE_FN);
    org.apache.spark.api.java.JavaRDD<org.apache.spark.mllib.recommendation.Rating> trainRatingData = parsedToRatingRDD(parsedRDD);
    trainRatingData = aggregateScores(trainRatingData);
    org.apache.spark.mllib.recommendation.MatrixFactorizationModel model;
    if (implicit) {
        model = org.apache.spark.mllib.recommendation.ALS.trainImplicit(trainRatingData.rdd(), features, iterations, lambda, alpha);
    } else {
        model = org.apache.spark.mllib.recommendation.ALS.train(trainRatingData.rdd(), features, iterations, lambda);
    }
    java.util.Map<java.lang.Integer, java.lang.String> reverseIDLookup = parsedRDD.flatMapToPair(new com.cloudera.oryx.app.mllib.als.ALSUpdate.ToReverseLookupFn()).reduceByKey(com.cloudera.oryx.lambda.fn.Functions.<java.lang.String>last()).collectAsMap();
    reverseIDLookup = new java.util.HashMap<>(reverseIDLookup);
    org.dmg.pmml.PMML pmml = com.cloudera.oryx.app.mllib.als.ALSUpdate.mfModelToPMML(model, features, lambda, alpha, implicit, candidatePath, reverseIDLookup);
    com.cloudera.oryx.app.mllib.als.ALSUpdate.unpersist(model);
    return pmml;
}

