        org.junit.Assert.assertEquals("UP", update.getFirst());
        java.util.List<?> fields = com.cloudera.oryx.lambda.AbstractLambdaIT.MAPPER.readValue(update.getSecond(), java.util.List.class);
        int clusterID = ((java.lang.Integer) (fields.get(0)));
        double[] updatedCenter = com.cloudera.oryx.lambda.AbstractLambdaIT.MAPPER.convertValue(fields.get(1), double[].class);
        int updatedClusterSize = ((java.lang.Integer) (fields.get(2)));
        clusterInfos.put(clusterID, new com.cloudera.oryx.app.kmeans.ClusterInfo(clusterID, updatedCenter, updatedClusterSize));
    }
    org.junit.Assert.assertEquals(3, clusterInfos.size());
    for (com.cloudera.oryx.app.kmeans.ClusterInfo clusterInfo : clusterInfos.values()) {
        int id = clusterInfo.getID();
        org.dmg.pmml.Cluster cluster = clusters.get(id);
        java.lang.String[] tokens = com.cloudera.oryx.common.text.TextUtils.parseDelimited(cluster.getArray().getValue(), ' ');
