        org.junit.Assert.assertArrayEquals(updatedCenter, com.cloudera.oryx.app.speed.kmeans.MockKMeansInputGenerator.UPDATE_POINTS[id], 0.1);
        long updatedClusterSize = clusterInfo.getCount();
