    java.nio.file.Path tempDir = getTempDir();
    java.nio.file.Path dataDir = tempDir.resolve("data");
    java.nio.file.Path modelDir = tempDir.resolve("model");
    java.util.Map<java.lang.String, java.lang.Object> overlayConfig = new java.util.HashMap<>();
    overlayConfig.put("oryx.batch.update-class", com.cloudera.oryx.ml.MockMLUpdate.class.getName());
    com.cloudera.oryx.common.settings.ConfigUtils.set(overlayConfig, "oryx.batch.storage.data-dir", dataDir);
    com.cloudera.oryx.common.settings.ConfigUtils.set(overlayConfig, "oryx.batch.storage.model-dir", modelDir);
    overlayConfig.put("oryx.batch.streaming.generation-interval-sec", com.cloudera.oryx.ml.SimpleMLUpdateIT.GEN_INTERVAL_SEC);
    overlayConfig.put("oryx.batch.streaming.block-interval-sec", com.cloudera.oryx.ml.SimpleMLUpdateIT.BLOCK_INTERVAL_SEC);
    overlayConfig.put("oryx.ml.eval.test-fraction", com.cloudera.oryx.ml.SimpleMLUpdateIT.TEST_FRACTION);
    com.typesafe.config.Config config = com.cloudera.oryx.common.settings.ConfigUtils.overlayOn(overlayConfig, getConfig());
    startMessaging();
    java.util.List<java.lang.Integer> trainCounts = new java.util.ArrayList<>();
    java.util.List<java.lang.Integer> testCounts = new java.util.ArrayList<>();
    com.cloudera.oryx.ml.MockMLUpdate.setCountHolders(trainCounts, testCounts);
    startServerProduceConsumeTopics(config, com.cloudera.oryx.ml.SimpleMLUpdateIT.DATA_TO_WRITE, com.cloudera.oryx.ml.SimpleMLUpdateIT.WRITE_INTERVAL_MSEC);
    while (trainCounts.size() > testCounts.size()) {
        testCounts.add(0);
    } 
    com.cloudera.oryx.ml.SimpleMLUpdateIT.log.info("trainCounts = {}", trainCounts);
    com.cloudera.oryx.ml.SimpleMLUpdateIT.log.info("testCounts = {}", testCounts);
    com.cloudera.oryx.lambda.AbstractBatchIT.checkOutputData(dataDir, com.cloudera.oryx.ml.SimpleMLUpdateIT.DATA_TO_WRITE);
    com.cloudera.oryx.lambda.AbstractBatchIT.checkIntervals(trainCounts.size(), com.cloudera.oryx.ml.SimpleMLUpdateIT.DATA_TO_WRITE, com.cloudera.oryx.ml.SimpleMLUpdateIT.WRITE_INTERVAL_MSEC, com.cloudera.oryx.ml.SimpleMLUpdateIT.GEN_INTERVAL_SEC);
    org.junit.Assert.assertEquals(testCounts.size(), trainCounts.size());
    org.apache.commons.math3.random.RandomGenerator random = com.cloudera.oryx.common.random.RandomManager.getRandom();
    int lastTotalTrainCount = 0;
    int lastTestCount = 0;
    for (int i = 0; i < testCounts.size(); i++) {
        int totalTrainCount = trainCounts.get(i);
        int testCount = testCounts.get(i);
        int newTrainInGen = totalTrainCount - (lastTotalTrainCount + lastTestCount);
        if (newTrainInGen == 0) {
            continue;
        }
        lastTotalTrainCount = totalTrainCount;
        lastTestCount = testCount;
        int totalNew = testCount + newTrainInGen;
        org.apache.commons.math3.distribution.IntegerDistribution dist = new org.apache.commons.math3.distribution.BinomialDistribution(random, totalNew, com.cloudera.oryx.ml.SimpleMLUpdateIT.TEST_FRACTION);
        double probability;
        if (testCount < dist.getNumericalMean()) {
            probability = dist.cumulativeProbability(testCount);
        } else {
            probability = 1.0 - dist.cumulativeProbability(testCount);
        }
        com.cloudera.oryx.ml.SimpleMLUpdateIT.log.info("Probability of observing {} as {} sample of {}: {}", testCount, com.cloudera.oryx.ml.SimpleMLUpdateIT.TEST_FRACTION, totalNew, probability);
        org.junit.Assert.assertTrue(probability >= 0.001);
    }
}

