    pmml.getModels().add(new org.dmg.pmml.ClusteringModel(org.dmg.pmml.MiningFunctionType.CLUSTERING, ClusteringModel.ModelClass.CENTER_BASED, clusters.size(), miningSchema, new org.dmg.pmml.ComparisonMeasure(ComparisonMeasure.Kind.DISTANCE).withMeasure(new org.dmg.pmml.SquaredEuclidean()), clusteringFields, clusters));
