        java.lang.String name = "FEATURE" + i;
        org.togglz.core.Feature feature = new org.togglz.core.repository.file.FileBasedStateRepositoryConcurrencyTest.TestFeature(name);
        final org.togglz.core.repository.FeatureState state = new org.togglz.core.repository.FeatureState(feature).setStrategyId("strategy-for-" + name).setParameter("param-of-" + name, "some-value-of-" + name);
        executor.submit(new java.lang.Runnable() {
            @java.lang.Override
            public void run() {
                repo.setFeatureState(state);
            }
        });
    }
    executor.shutdown();
    executor.awaitTermination(5, java.util.concurrent.TimeUnit.SECONDS);
    for (int i = 0; i < NUMBER_OF_FEATURES; i++) {
        java.lang.String name = "FEATURE" + i;
        org.togglz.core.repository.file.FileBasedStateRepositoryConcurrencyTest.TestFeature feature = new org.togglz.core.repository.file.FileBasedStateRepositoryConcurrencyTest.TestFeature(name);
        org.togglz.core.repository.FeatureState state = repo.getFeatureState(feature);
        assertThat(state).isNotNull();
        assertThat(state.getStrategyId()).isEqualTo("strategy-for-" + name);
        assertThat(state.getParameterMap()).hasSize(1).contains(org.assertj.core.data.MapEntry.entry("param-of-" + name, "some-value-of-" + name));
    }
}

