        }
        org.togglz.core.user.FeatureUser user = userProvider.getCurrentUser();
        for (org.togglz.core.spi.ActivationStrategy strategy : strategyProvider.getActivationStrategies()) {
            if (strategy.getId().equalsIgnoreCase(strategyId)) {
