    if ((now - lastCheck) > minCheckInterval) {
        lastCheck = now;
        if (file.lastModified() > lastRead) {
            java.io.FileInputStream stream = null;
            try {
                stream = new java.io.FileInputStream(file);
                java.util.Properties newValues = new java.util.Properties();
                newValues.load(stream);
                values = newValues;
                lastRead = java.lang.System.currentTimeMillis();
                org.togglz.core.repository.file.ReloadablePropertiesFile.log.info("Reloaded file: " + file.getCanonicalPath());
            } catch (java.io.FileNotFoundException e) {
                org.togglz.core.repository.file.ReloadablePropertiesFile.log.debug("File not found: " + file);
            } catch (java.io.IOException e) {
                org.togglz.core.repository.file.ReloadablePropertiesFile.log.error("Failed to read file", e);
            } finally {
                org.togglz.core.util.IOUtils.close(stream);
            }
        }
    }
}

