    return new org.junit.runners.model.Statement() {
        @java.lang.Override
        public void evaluate() throws java.lang.Throwable {
            try {
                if (featureManager != null) {
                    throw new java.lang.IllegalStateException("Found existing TestFeatureManager");
                }
                org.togglz.junit.WithFeature withFeature = description.getAnnotation(org.togglz.junit.WithFeature.class);
                for (java.util.Set<org.togglz.core.Feature> activeFeatures : variants) {
                    featureManager = new org.togglz.testing.TestFeatureManager(featureClass);
                    for (org.togglz.core.Feature feature : activeFeatures) {
                        featureManager.enable(feature);
                    }
                    org.togglz.testing.TestFeatureManagerProvider.setFeatureManager(featureManager);
                    org.togglz.core.context.FeatureContext.clearCache();
                    if (withFeature != null) {
                        if (variants.size() == 1) {
                            for (java.lang.String featureName : withFeature.value()) {
                                org.togglz.core.Feature feature = new org.togglz.core.util.NamedFeature(featureName);
                                if (withFeature.disable()) {
                                    disable(feature);
                                } else {
                                    enable(feature);
                                }
                            }
                        } else {
                            org.togglz.junit.TogglzRule.log.info("Ignoring @ViewFeature because the rule defines feature variations");
                        }
                    }
                    base.evaluate();
                }
            } finally {
                featureManager = null;
                org.togglz.testing.TestFeatureManagerProvider.setFeatureManager(null);
                org.togglz.core.context.FeatureContext.clearCache();
            }
        }
    };
}

