    savedFeatureState.setStrategyId(org.togglz.core.activation.UsernameActivationStrategy.ID);
    savedFeatureState.setParameter(org.togglz.core.activation.UsernameActivationStrategy.PARAM_USERS, "user1, user2, user3");
    stateRepository.setFeatureState(savedFeatureState);
    org.togglz.core.repository.FeatureState loadedFeatureState = stateRepository.getFeatureState(org.togglz.zookeeper.ZookeeperStateRepositoryTest.TestFeature.FEATURE);
    org.junit.Assert.assertThat(reflectionEquals(savedFeatureState, loadedFeatureState), org.hamcrest.CoreMatchers.is(true));
    org.togglz.zookeeper.FeatureStateStorageWrapper externallySetStateWrapper = new org.togglz.zookeeper.FeatureStateStorageWrapper();
    org.togglz.core.repository.FeatureState externallySetState = new org.togglz.core.repository.FeatureState(org.togglz.zookeeper.ZookeeperStateRepositoryTest.TestFeature.FEATURE);
    com.fasterxml.jackson.databind.ObjectMapper objectMapper = new com.fasterxml.jackson.databind.ObjectMapper();
    final java.lang.String json = objectMapper.writeValueAsString(externallySetStateWrapper);
    final java.util.concurrent.CountDownLatch latch = new java.util.concurrent.CountDownLatch(1);
    new java.lang.Thread(new java.lang.Runnable() {
        @java.lang.Override
        public void run() {
            try {
                serverClientPair.client.setData().forPath(org.togglz.zookeeper.ZookeeperStateRepositoryTest.TEST_ZNODE + "/FEATURE", json.getBytes("UTF-8"));
                latch.countDown();
            } catch (java.lang.Exception e) {
                e.printStackTrace();
            }
        }
    }).start();
    latch.await(2, java.util.concurrent.TimeUnit.SECONDS);
    java.lang.Thread.sleep(25);
    loadedFeatureState = stateRepository.getFeatureState(org.togglz.zookeeper.ZookeeperStateRepositoryTest.TestFeature.FEATURE);
    org.junit.Assert.assertThat(reflectionEquals(externallySetState, loadedFeatureState), org.hamcrest.CoreMatchers.is(true));
}

