            java.sql.PreparedStatement statement = connection.prepareStatement(insertTableName(sql));
            try {
                statement.setString(1, feature.name());
                java.sql.ResultSet resultSet = statement.executeQuery();
                try {
                    if (resultSet.next()) {
                        boolean enabled = resultSet.getInt(org.togglz.core.repository.jdbc.Columns.FEATURE_ENABLED) > 0;
                        org.togglz.core.repository.FeatureState state = new org.togglz.core.repository.FeatureState(feature, enabled);
                        java.lang.String strategyId = resultSet.getString(org.togglz.core.repository.jdbc.Columns.STRATEGY_ID);
                        if (org.togglz.core.util.Strings.isNotBlank(strategyId)) {
                            state.setStrategyId(strategyId.trim());
                        }
                        java.lang.String paramData = resultSet.getString(org.togglz.core.repository.jdbc.Columns.STRATEGY_PARAMS);
                        if (org.togglz.core.util.Strings.isNotBlank(paramData)) {
                            java.util.Map<java.lang.String, java.lang.String> params = serializer.deserialize(paramData);
                            for (java.util.Map.Entry<java.lang.String, java.lang.String> param : params.entrySet()) {
                                state.setParameter(param.getKey(), param.getValue());
                            }
                        }
                        return state;
                    }
                } finally {
                    org.togglz.core.util.DbUtils.closeQuietly(resultSet);
                }
            } finally {
                org.togglz.core.util.DbUtils.closeQuietly(statement);
            }
        } finally {
            org.togglz.core.util.DbUtils.closeQuietly(connection);
        }
    } catch (java.sql.SQLException e) {
        throw new java.lang.IllegalStateException("Failed to fetch the feature's state from the database", e);
    }
    return null;
}

