    if (state == null) {
        state = getMetaData(feature).getDefaultFeatureState();
    }
    if (state.isEnabled()) {
        java.lang.String strategyId = state.getStrategyId();
        if ((strategyId == null) || strategyId.isEmpty()) {
            return true;
        }
        org.togglz.core.user.FeatureUser user = userProvider.getCurrentUser();
        for (org.togglz.core.spi.ActivationStrategy strategy : strategyProvider.getActivationStrategies()) {
            if (strategy.getId().equalsIgnoreCase(strategyId)) {
                return strategy.isActive(state, user);
            }
        }
    }
    return false;
}

